INSERT INTO `attractions` (`attraction_id`, `attraction_name`, `attraction_type`, `attraction_description`, `attraction_img`) VALUES
(1, 'ריידר שחף', 'אופניים', '🚵 ריידר שחף – חוף הכרמל\r\nטיול שטח קליל עם נוף לים – אווירה מושלמת לשקיעה\r\nבריידר שחף תוכלו ליהנות מרכיבה שקטה לאורך קו החוף של הכרמל. המסלול עובר ליד חופים בתוליים, מרחבים פתוחים וטבע פראי שנושק לים.\r\nהפעילות משלבת רכיבה על כלי איזיריידר יציבים במיוחד, עצירות לתמונות, והסברים מרתקים על האזור.\r\nאטרקציה שמתאימה לזוגות, משפחות וגם לקבוצות חברים – והכל באווירה נינוחה ומזמינה, עם משב רוח מהים.\r\n\r\nמה כלול:\r\n📍 מיקום מושלם ליד חוף הים\r\n🛴 רכיבה מודרכת באיזיריידרים\r\n👣 מסלול בטוח ומישורי\r\n📸 נקודות צילום לאורך החוף\r\n🌅 תצפית שקיעה עוצרת נשימה\r\n🎧 הדרכה באוזנייה\r\n⏳ משך הטיול: כשעה וחצי\r\n🍉 ניתן לשלב עם פיקניק\r\n🧢 חובה כובע ונעליים סגורות', 'img-attraction01.jpg, img-attraction1.jpg'),
(2, 'ריידר הכרמל', 'אופניים', '🚴 ריידר הכרמל\r\nרכיבה חווייתית בין עצי האורן של יערות הכרמל\r\nריידר הכרמל הוא אטרקציה מושלמת לחובבי טבע, שטח והרפתקאות רכות. טיול מודרך על כלים חשמליים שקטים (כמו איזיריידרים), לאורך שבילים קסומים בלב היער.\r\nהמסלול עובר בין נקודות תצפית מדהימות, עצי אורן מרשימים וניחוחות של טבע ישראלי אמיתי. מתאים מאוד למשפחות עם ילדים, קבוצות חברים וגם לזוגות שמחפשים פעילות רומנטית ומיוחדת.\r\nכולל הדרכה בטיחותית, עצירות להסברים ולצילומים, ותחושת רוגע וניתוק מהשגרה.\r\n\r\nמה כלול:\r\n📞 יש לתאם מראש\r\n🚲 כלי רכיבה חשמליים בטוחים\r\n👨‍👩‍👧‍👦 מתאים לכל הגילאים\r\n🧢 חובה נעליים סגורות וקסדה\r\n📸 נקודות צילום מדהימות\r\n🧃 הפסקת שתייה קלה בדרך\r\n🗺 מסלול מעגלי של כשעה וחצי\r\n💬 מדריך מלווה עם ידע על האזור\r\n🚗 חנייה בשפע\r\n', 'img-attraction02.jpg, img-attraction2.jpg'),
(3, 'אגמון החולה', 'אופנוע', '🌿 אגמון החולה – טבע מדהים בלב הצפון\r\n\r\nבצפון הארץ, בין הכנרת לגולן, נמצא אגמון החולה – אחד מאזורי הטבע המיוחדים ביותר בישראל. זהו אזור ביצות ושמורת טבע שמושך אליו אלפי ציפורים נודדות מדי שנה, ומציע חוויות טיול מרהיבות לכל המשפחה. אם אתם חובבי טבע, בעלי חיים או פשוט מחפשים מקום שקט להירגע בו – אגמון החולה הוא היעד המושלם.\r\n\r\n🗺 מה תמצאו שם?\r\n🦅 צפרות מרהיבה – תצפיות על ציפורים נודדות\r\n🚶‍♀ מסלולי הליכה ואופניים ברחבי השמורה\r\n🚣‍♂ סיורים בסירה בין האגמים\r\n🌿 צמחייה טבעית ומרשימה\r\n👣 שבילים נגישים להליכה קלה\r\n\r\n💡 חשוב לדעת:\r\n🆓 הכניסה בתשלום\r\n📅 כדאי להזמין מראש סיור מודרך או השכרת אופניים\r\n🧑‍👩‍👧‍👦 מתאים למשפחות, חובבי צפרות וטיולים בטבע\r\n🐦 המקום הוא אתר חשוב לציפורים נודדות\r\n🚗 חניה מסודרת בכניסה לאגמון\r\n\r\n📲 איך מתאמים ביקור?\r\n📞 ניתן להזמין סיור דרך האתר או טלפון\r\n🕒 הפארק פתוח במהלך כל השנה, עם שעות פעילות משתנות\r\n\r\n🧭 טיפ מאיתנו:\r\n🦆 אל תשכחו לקחת אתכם משקפת מצלמה – הציפורים כאן פשוט מהממות, והנופים על האגמון הם כמו תמונה חיה!', 'img-attraction03.jpg, img-attraction3.jpg'),
(4, 'איזי ריידר תל אביב', 'אופנוע', '🛵 איזי ריידר תל אביב – גלגלים בעיר הגדולה\r\n\r\nרוצים לחוות את תל אביב בדרך אחרת? סיורי איזי ריידר בתל אביב ייקחו אתכם ברחבי העיר על רכבים חשמליים, ויתנו לכם לראות את המקומות הכי אייקוניים בעיר בצורה מהנה וחדשנית. מהחופים היפיפיים ועד יפו העתיקה, אתם תראו את תל אביב מנקודת מבט חדשה ומרתקת.\r\n\r\n🗺 מה תמצאו שם?\r\n🛵 רכיבה על רכבים חשמליים (Ezraider) ברחבי העיר\r\n🌆 סיורים מודרכים בעיר העתיקה, החופים והפארק\r\n📸 נקודות תצפית מדהימות לתמונות\r\n🏙 חווית רכיבה עירונית בשילוב טבע ועיר\r\n\r\n💡 חשוב לדעת:\r\n🆓 הכניסה בתיאום מראש\r\n📅 סיורים מסודרים בעונות שונות – כדאי להזמין מראש\r\n🚶‍♀ מתאים למשפחות, זוגות וקבוצות\r\n\r\n📲 איך מתאמים ביקור?\r\n📞 תיאום סיור דרך האתר או בטלפון\r\n🕒 שעות סיור משתנות – כדאי לבדוק מראש\r\n\r\n🧭 טיפ מאיתנו:\r\n🧢 אל תשכחו כובע, משקפי שמש ומצלמה – תל אביב היא עיר של נופים מרהיבים!\r\n', 'img-attraction04.jpg, img-attraction4.jpg'),
(5, 'אגמון החולה', 'אופנוע', '🌵 פארק תמנע – פלאים גיאולוגיים בלב הערבה\r\n\r\nבדרום הארץ, בין המדבר לאילת, שוכן פארק תמנע – אחת הפנינות הגיאולוגיות של ישראל. הפארק מציע מסלולים רגליים וטיולי ג\'יפים, אתרים ארכיאולוגיים כמו המקדש המצרי, סלעים מרהיבים כמו הפטרייה, ומגוון פעילויות לכל המשפחה. כל ביקור בפארק תמנע הוא חוויה עוצרת נשימה של טבע, היסטוריה ותרבות.\r\n\r\n🗺 מה תמצאו שם?\r\n🏜 נופים מדבריים עם סלעים צבעוניים וייחודיים\r\n⛺ אפשרויות קמפינג וטיולי ג\'יפים\r\n🛶 שיט על האגם בפארק\r\n🧳 מסלולי הליכה לכל הרמות\r\n📜 אתרים ארכיאולוגיים מרתקים\r\n\r\n💡 חשוב לדעת:\r\n🆓 הכניסה בתשלום\r\n📅 מומלץ להזמין מקום מראש – במיוחד בעונות החמות\r\n🔥 לא ניתן להדליק מדורות – יש לבדוק את כללי האתר\r\n🚶‍♀ מתאים למשפחות, מטיילים רגליים ורוכבי אופניים\r\n\r\n📲 איך מתאמים ביקור?\r\n📞 תיאום דרך אתר פארק תמנע או טלפון\r\n🕒 הפארק פתוח בשעות היום\r\n\r\n🧭 טיפ מאיתנו:\r\n🏜 בואו להכיר את הפארק גם בשעות ערב – השמש שוקעת מאחורי הסלעים בצורה מרהיבה!', 'img-attraction05.jpg, img-attraction5.jpg');

