INSERT INTO `camping` (`camping_location_name`, `camping_description`, `camping_duration`, `camping_img`) VALUES
('גן לאומי בית גוברין', 'גן לאומי בית גוברין\r\nאתר ארכיאולוגי מדהים עם מערות ומסלולי הליכה שמתאימים לכל גיל.\r\n\r\n⛏ מערות עתיקות\r\n🥾 מסלולים\r\n🧒 מתאים לילדים\r\n🧺 אזורי פיקניק\r\n🅿 חנייה\r\n🚻 שירותים\r\n📞 יש להזמין מראש\r\n', 'לפחות לילה', 'img-camping06.jpg, img-camping6.jpg'),
('חוף פלמחים ', 'חוף פלמחים (החלק הדרומי)\r\nרצועת חוף שקטה עם גישה נוחה, פינות ישיבה, ושקיעות עוצרות נשימה.\r\n\r\n🌊 חוף ים\r\n🧺 פיקניק\r\n🔥 מנגל\r\n🚿 מקלחות\r\n🅿 חנייה\r\n🎟 כניסה בתשלום\r\n🐾 מתאים לכלבים\r\n🕶 מתאים לקיץ', 'לפחות לילה', 'img-camping08.jpg, img-camping8.jpg'),
('חורשת טל', 'חורשת טל\r\nשמורת טבע עם מים זורמים, בריכה טבעית, צמחייה עשירה והמון פינות ישיבה.\r\n\r\n🏊‍♂ בריכת טבע\r\n🧺 פיקניק\r\n🔥 אפשרות למנגל\r\n🛏 אפשרות לינה\r\n🚿 מקלחות\r\n🅿 חנייה בשפע\r\n🌲 טבע פראי\r\n🎟 תשלום בכניסה.', 'לפחות לילה', 'img-camping05.jpg, img-camping5.jpg'),
('חניון יער בן שמן', 'חניון יער בן שמן\r\nמוקף יערות ירוקים, המקום מושלם לקמפינג ולבילוי בטבע למשפחות וקבוצות.\r\n\r\n🏕 אפשרות לקמפינג\r\n🅿 חנייה בשטח\r\n🥩 אפשרות למנגל\r\n👨‍👩‍👧‍👦 מגרש משחקים לילדים\r\n🚽 שירותים זמינים\r\n🪵 שולחנות פיקניק\r\n💸 כניסה בתשלום סמלי\r\n♿ נגיש לבעלי מוגבלויות.', 'לפחות לילה', 'img-camping02.jpg, img-camping2.jpg'),
('מצפה הר גילה', 'מצפה הר גילה\r\nמצפה מדהים בהרי יהודה עם תצפית רחבה על ירושלים וסביבתה. מתאים לזוגות ולמשפחות שרוצות לבלות בטבע וליהנות מהשקט.\r\n\r\n📞 דורש הזמנה מראש\r\n🥩 אפשרות למנגל\r\n🚗 חנייה לרכב\r\n🧻 שירותים ציבוריים\r\n🧺 מתאים לפיקניק\r\n👨‍👩‍👧‍👦 מתאים לילדים\r\n⛰ מסלול הליכה בקרבת מקום\r\n🐾 ניתן להכניס כלבים\r\n💔 לא נגיש לבעלי מוגבלויות.', 'לפחות לילה', 'img-camping01.jpg, img-camping1.jpg'),
('מצפה כרמים', 'מצפה כרמים\r\nמקום מבודד ומרהיב עם תצפית על בקעת הירדן. מושלם לשקיעות וארוחות בטבע.\r\n\r\n🌄 נוף מהמם\r\n🪵 שולחנות\r\n🔥 מנגל\r\n🅿 חנייה\r\n🕯 מתאים לערב\r\n❤ אווירה רומנטית\r\n🧒 לא מותאם במיוחד לילדים.', 'לפחות לילה', 'img-camping07.jpg, img-camping7.jpg'),
('עין חוד תצפית', '\r\nעין חוד תצפית\r\nתצפית קסומה המשקיפה על חיפה והים, נקודה שקטה עם נוף רומנטי.\r\n\r\n❤ מקום רומנטי\r\n🌅 תצפית שקיעה\r\n🚶 מסלול קל בסביבה\r\n🅿 חנייה נוחה\r\n🧺 פינות ישיבה\r\n🐾 ידידותי לחיות מחמד', 'לפחות לילה', 'img-camping04.jpg, img-camping4.jpg'),
('פארק קנדה (איילון)', '\r\nפארק טבע עצום עם מסלולים, אתרי מים עתיקים ונקודות תצפית מרהיבות.\r\n\r\n🥾 מסלולי הליכה\r\n🧺 מתאים לפיקניק\r\n🔥 אפשרות למנגל\r\n🚽 שירותים\r\n🧒 אזור משחקים\r\n🌸 מתאים לכל עונות השנה\r\n🐾 כניסה עם כלבים', 'לפחות לילה', 'img-camping03.jpg, img-camping3.jpg');

